import axios from "axios";
import React, { useEffect } from "react";
import { useState } from "react";
import {
  Accordion,
  AccordionButton,
  Badge,
  Button,
  Card,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import MainScreen from "../../Components/MainScreen";
const date = new Date();
const currentTime = date.getHours();

let greeting;
if (currentTime >= 6 && currentTime < 12) {
  greeting = "Good Morning";
} else if (currentTime > 12 && currentTime <= 18) {
  greeting = "Quite an Afternoon";
} else {
  greeting = "Long Night";
}

const MyNotes = () => {
  const [notes, setNotes] = useState([]);
  const deleteHandler = (id) => {
    if (window.confirm("Are you sure?")) {
    }
  };
  const fetchNotes = async () => {
    const { data } = await axios.get("/api/notes");
    setNotes(data);
  };
  console.log(notes);
  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <MainScreen title={greeting}>
      <Link to="/createnote">
        <Button
          variant="info"
          className="mt-2 mb-2"
          // style={{ marginUp: 5, marginLeft: 10, marginBottom: 6 }}
          size="lg"
        >
          New Note
        </Button>
      </Link>
      {notes.map((note) => (
        <Accordion>
          <Accordion.Item>
            <Card style={{ margin: 10 }} key={note._id}>
              <Card.Header style={{ display: "flex" }}>
                <span
                  style={{
                    color: "black",
                    textDecoration: "none",
                    flex: 1,
                    cursor: "pointer",
                    alignSelf: "center",
                    fontSize: 18,
                  }}
                >
                  <AccordionButton as={Card.Text} variant="link" eventKey="0">
                    {note.title}
                  </AccordionButton>
                </span>
                <div>
                  <Button
                    className="mx-2"
                    variant="secondary"
                    href={`/note/${note.id}`}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="danger"
                    className="mx-2"
                    onClick={deleteHandler()}
                  >
                    Delete
                  </Button>
                </div>
              </Card.Header>
              <Accordion.Collapse eventKey="0">
                <Card.Body>
                  <h4>
                    <Badge bg="warning" text="light">
                      Category - {note.category}{" "}
                    </Badge>
                  </h4>

                  <blockquote className="blockquote mb-0">
                    <p>{note.content}</p>
                    <footer className="blockquote-footer">
                      Creater on - date
                    </footer>
                  </blockquote>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion.Item>
        </Accordion>
      ))}
    </MainScreen>
  );
};

export default MyNotes;
